#[cfg(test)]
struct BenchData {
    signal_1byte: Vec<Complex<f32>>,
    signal_2bytes: Vec<Complex<f32>>,
    signal_4bytes: Vec<Complex<f32>>,
    signal_8bytes: Vec<Complex<f32>>,
    signal_16bytes: Vec<Complex<f32>>,
    signal_32bytes: Vec<Complex<f32>>,
    signal_64bytes: Vec<Complex<f32>>,
    signal_128bytes: Vec<Complex<f32>>,
    signal_256bytes: Vec<Complex<f32>>,
    signal_512bytes: Vec<Complex<f32>>,
    signal_1024bytes: Vec<Complex<f32>>,
    signal_2048bytes: Vec<Complex<f32>>,
    mods: Modulators,
    demods: Demodulators,
}

#[cfg(test)]
impl Default for BenchData {
    fn default() -> Self {
        let samples_per_symbol = (SAMPLE_RATE / BAUD_RATE) as usize;
        let mods = Modulators::new(samples_per_symbol, SAMPLE_RATE);
        let demods = Demodulators::new(samples_per_symbol, SAMPLE_RATE);
        BenchData {
            signal_1byte: mods.fsk(BYTE_1),
            signal_2bytes: mods.fsk(BYTES_2),
            signal_4bytes: mods.fsk(BYTES_4),
            signal_8bytes: mods.fsk(BYTES_8),
            signal_16bytes: mods.fsk(BYTES_16),
            signal_32bytes: mods.fsk(BYTES_32),
            signal_64bytes: mods.fsk(BYTES_64),
            signal_128bytes: mods.fsk(BYTES_128),
            signal_256bytes: mods.fsk(BYTES_256),
            signal_512bytes: mods.fsk(BYTES_512),
            signal_1024bytes: mods.fsk(BYTES_1024),
            signal_2048bytes: mods.fsk(BYTES_2048),
            mods,
            demods,
        }
    }
}

lazy_static! {
    static ref DATA:BenchData = BenchData::default();
}

/// Modulation benchmarks
fn fsk_mod_1byte() { DATA.mods.fsk(BYTE_1); }

fn fsk_mod_2bytes() { DATA.mods.fsk(BYTES_2); }

fn fsk_mod_4bytes() { DATA.mods.fsk(BYTES_4); }

fn fsk_mod_8bytes() { DATA.mods.fsk(BYTES_8); }

fn fsk_mod_16bytes() { DATA.mods.fsk(BYTES_16); }

fn fsk_mod_32bytes() { DATA.mods.fsk(BYTES_32); }

fn fsk_mod_64bytes() { DATA.mods.fsk(BYTES_64); }

fn fsk_mod_128bytes() { DATA.mods.fsk(BYTES_128); }

fn fsk_mod_256bytes() { DATA.mods.fsk(BYTES_256); }

fn fsk_mod_512bytes() { DATA.mods.fsk(BYTES_512); }

fn fsk_mod_1024bytes() { DATA.mods.fsk(BYTES_1024); }

fn fsk_mod_2048bytes() { DATA.mods.fsk(BYTES_2048); }


/// Demodulation benchmarks
fn fsk_demod_1byte() { DATA.demods.fsk(DATA.signal_1byte.clone()); }

fn fsk_demod_2bytes() { DATA.demods.fsk(DATA.signal_2bytes.clone()); }

fn fsk_demod_4bytes() { DATA.demods.fsk(DATA.signal_4bytes.clone()); }

fn fsk_demod_8bytes() { DATA.demods.fsk(DATA.signal_8bytes.clone()); }

fn fsk_demod_16bytes() { DATA.demods.fsk(DATA.signal_16bytes.clone()); }

fn fsk_demod_32bytes() { DATA.demods.fsk(DATA.signal_32bytes.clone()); }

fn fsk_demod_64bytes() { DATA.demods.fsk(DATA.signal_64bytes.clone()); }

fn fsk_demod_128bytes() { DATA.demods.fsk(DATA.signal_128bytes.clone()); }

fn fsk_demod_256bytes() { DATA.demods.fsk(DATA.signal_256bytes.clone()); }

fn fsk_demod_512bytes() { DATA.demods.fsk(DATA.signal_512bytes.clone()); }

fn fsk_demod_1024bytes() { DATA.demods.fsk(DATA.signal_1024bytes.clone()); }

fn fsk_demod_2048bytes() { DATA.demods.fsk(DATA.signal_2048bytes.clone()); }


pub fn fsk_benchmark(c: &mut Criterion) {
    // Modulators benchmarks
    c.bench_function("fsk mod 1 byte", |b| b.iter(fsk_mod_1byte));
    c.bench_function("fsk mod 2 bytes", |b| b.iter(fsk_mod_2bytes));
    c.bench_function("fsk mod 4 bytes", |b| b.iter(fsk_mod_4bytes));
    c.bench_function("fsk mod 8 bytes", |b| b.iter(fsk_mod_8bytes));
    c.bench_function("fsk mod 16 bytes", |b| b.iter(fsk_mod_16bytes));
    c.bench_function("fsk mod 32 bytes", |b| b.iter(fsk_mod_32bytes));
    c.bench_function("fsk mod 64 bytes", |b| b.iter(fsk_mod_64bytes));
    c.bench_function("fsk mod 128 bytes", |b| b.iter(fsk_mod_128bytes));
    c.bench_function("fsk mod 256 bytes", |b| b.iter(fsk_mod_256bytes));
    c.bench_function("fsk mod 512 bytes", |b| b.iter(fsk_mod_512bytes));
    c.bench_function("fsk mod 1024 bytes", |b| b.iter(fsk_mod_1024bytes));
    c.bench_function("fsk mod 2048 bytes", |b| b.iter(fsk_mod_2048bytes));


    // Demodulators benchmarks
    c.bench_function("fsk demod 1 byte", |b| b.iter(fsk_demod_1byte));
    c.bench_function("fsk demod 2 bytes", |b| b.iter(fsk_demod_2bytes));
    c.bench_function("fsk demod 4 bytes", |b| b.iter(fsk_demod_4bytes));
    c.bench_function("fsk demod 8 bytes", |b| b.iter(fsk_demod_8bytes));
    c.bench_function("fsk demod 16 bytes", |b| b.iter(fsk_demod_16bytes));
    c.bench_function("fsk demod 32 bytes", |b| b.iter(fsk_demod_32bytes));
    c.bench_function("fsk demod 64 bytes", |b| b.iter(fsk_demod_64bytes));
    c.bench_function("fsk demod 128 bytes", |b| b.iter(fsk_demod_128bytes));
    c.bench_function("fsk demod 256 bytes", |b| b.iter(fsk_demod_256bytes));
    c.bench_function("fsk demod 512 bytes", |b| b.iter(fsk_demod_512bytes));
    c.bench_function("fsk demod 1024 bytes", |b| b.iter(fsk_demod_1024bytes));
    c.bench_function("fsk demod 2048 bytes", |b| b.iter(fsk_demod_2048bytes));
}


criterion_group!(benches, fsk_benchmark);
criterion_main!(benches);