    // Modulators benchmarks
    c.bench_function("qpsk mod 1 byte", |b| b.iter(qpsk_mod_1byte));
    c.bench_function("qpsk mod 2 bytes", |b| b.iter(qpsk_mod_2bytes));
    c.bench_function("qpsk mod 4 bytes", |b| b.iter(qpsk_mod_4bytes));
