    c.bench_function("qpsk mod 128 bytes", |b| b.iter(qpsk_mod_128bytes));
    c.bench_function("qpsk mod 256 bytes", |b| b.iter(qpsk_mod_256bytes));
    c.bench_function("qpsk mod 512 bytes", |b| b.iter(qpsk_mod_512bytes));
    c.bench_function("qpsk mod 1024 bytes", |b| b.iter(qpsk_mod_1024bytes));
    c.bench_function("qpsk mod 2048 bytes", |b| b.iter(qpsk_mod_2048bytes));


    // Demodulators benchmarks
    c.bench_function("qpsk demod 1 byte", |b| b.iter(qpsk_demod_1byte));
    c.bench_function("qpsk demod 2 bytes", |b| b.iter(qpsk_demod_2bytes));
